import { useState } from 'react'
import logo from './logo.svg';
import './App.css';

function App() {
  let [title, setTitle] = useState(['ÎÇ®Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú', 'Í∞ïÎÇ® Ïö∞ÎèôÎßõÏßë', 'ÌååÏù¥Ïç¨ ÎèÖÌïô']);
