            <div className="list" key={i}>
              <h4 onClick={() => { setModal(!modal); setTitleIndex(i); }}>{a}
                <span onClick={(e) => {
                  e.stopPropagation();
                  let copy = [...likeCount];
                  copy[i]++;
                  setLikeCount(copy)
                }}>üëç</span> {likeCount[i]} </h4>
