      <button onClick={() => {
        let copy = [...title];
        copy[0] = 'Ïó¨Ïûê ÏΩîÌä∏ Ï∂îÏ≤ú';
        setTitle(copy);
      }}>Í≤åÏãúÍ∏Ä Î≥ÄÍ≤Ω</button>
      <button onClick={() => {
        let copy = [...title];
        copy.sort();
        setTitle(copy);
      }}>Í∞ÄÎÇòÎã§ Ï†ïÎ†¨</button>
      <input onChange={(e) => { setInput(e.target.value); }}></input>
      <button onClick={() => {
        let copy = [...title];
        copy.unshift(input);
        setTitle(copy)
      }}>Í∏ÄÎ∞úÌñâ</button>
      {
        modal ? <Modal title={title} titleIndex={titleIndex} onClick={() => { setModal(false) }}></Modal> : null
      }
    </div>
  );
}

function Modal(props) {
  return (
    <div className="modal">
      <h4>{props.title[props.titleIndex]}</h4>
      <p>ÎÇ†Ïßú</p>
